
public class Calc {

	public int sum(int i, int j) {
		return i + j;
	}

	public int sub(int i, int j) {
		// TODO Auto-generated method stub
		return i - j;
	}

	public int div(int i, int j) {
		// TODO Auto-generated method stub
		return i / j;
	}

	public int mult(int i, int j) {
		// TODO Auto-generated method stub
		return i * j;
	}

}
